package ca.blakey.monte_carlo.model;



/**
 * @author phill_000
 * Copyright (c) <2015> <Phillip Blakey>
 * 
 * This class contains a bunch of private variables and get and set methods that hold 
 * the data generated by a simulation.
 *
 */
public class Result {
	private long seed;
	private double mean;
	private double std;
	private double variance;
	private double time;
	private double executionTime;
	private int successes;
	private int trials;

	/**
	 * @param seedIn
	 * @param meanIn
	 * @param stdIn
	 * @param varianceIn
	 * @param timeIn
	 * @param excecutionTimeIn
	 * @param successesIn
	 * @param trialsIn
	 */
	public Result(long seedIn, double meanIn, double stdIn,
			double varianceIn, double timeIn, double excecutionTimeIn,
			int successesIn, int trialsIn){

		this.seed = seedIn;
		this.mean = meanIn;
		this.std = stdIn;
		this.variance = varianceIn;
		this.time = timeIn;
		this.executionTime = excecutionTimeIn;
		this.successes = successesIn;
		this.trials = trialsIn;
	}
	
	/**
	 * This constructor overloads the main constructor and is used in this class's JUnit 
	 * Test.
	 */
	public Result(){
		this.seed = 2;
		this.mean =2;
		this.std = 2;
		this.variance = 0;
		this.time = 0;
		this.executionTime = 0;
		this.successes = 0;
		this.trials = 0;
	}

	/**
	 * @return Returns seed.
	 */
	public long getSeed(){
		return this.seed;
	}

	/**
	 * @return Returns mean.
	 */
	public double getMean() {
		return this.mean;
	}

	/**
	 * @return Returns Standard Deviation.
	 */
	public double getStd() {
		return this.std;
	}
	/**
	 * @return Returns Variance.
	 */
	public double getVariance(){
		return this.variance;
	}
	/**
	 * @return Returns end Time.
	 */
	public double getTime(){
		return this.time;
	}
	/**
	 * @return Returns execution time
	 */
	public double getExecutionTime(){
		return this.executionTime;
	}
	/**
	 * @return Returns successes.
	 */
	public double getSuccesses(){
		return this.successes;
	}
	/**
	 * @return Returns the number of trials.
	 */
	public double getTrials(){
		return this.trials;
	}

	/**
	 * @param seedIn Sets the input seed.
	 */
	public void setSeed(long seedIn){
		this.seed = seedIn;
	}

	/**
	 * @param meanIn Sets the mean;
	 */
	public void setMean(double meanIn){
		this.mean = meanIn;
	}

	/**
	 * @param stdIn Sets the standard deviation.
	 */
	public void setStd(double stdIn){
		this.std = stdIn;
	}



}
